import { optimisticVerify } from "../optimisticVerify/index.js";
export const batchOptimisticVerify = async (connection, options, input) => {
  for (let i = 0; i < input.length; i++) {
    const result = await optimisticVerify(connection, options, input[i]);
    if (!result.success) {
      return {
        success: false,
        message: `Proof at index ${i} failed: ${result.message}`
      };
    }
  }
  return {
    success: true,
    message: 'Optimistic Verification Successful!'
  };
};